import { View, Text, StyleSheet } from "react-native";
import React from "react";

import { size, colors, fonts } from "../utils";

export default function Alerts({alert}) {
    const { severity, title } = alert;

    return (
        <View>
        <Text>{severity}: {title}</Text>
        </View>
    );
}
